mapper.select:

	select name, email, gender
	  from user
     where no = #{no }


select *
  from user
 where id = #{id }
   and password = #{password }


select no
  from user
 where email = #{email }

choose.when:

update user
   set name = #{name },
       gender = #{gender},
       password = #{password }
 where no = #{no }

choose.otherwise:

update user
   set name = #{name },
       gender = #{gender }
 where no = #{no }
